mapper.update:
	update users
   		set coin = #{coin}          
 	where user_id = #{userId}

mapper.insert:
insert into coin_history (
	user_id, coin, at_that_price, created_at, at_that_user_coin
)
values (
	#{userId},
	#{coin},
	#{atThatPrice},
	systimestamp, 


			#{afterBalance}


			(
				select nvl(u.coin, 0) + #{coin}
				  from users u
				 where u.user_id = #{userId}
			)


)

mapper.select:
	select nvl(u.coin, 0)
  	from users u
 	where u.user_id = #{value}
